<?php/** * Created by PhpStorm. * User: milo * Date: 15/3/23 * Time: 14:05 */namespace Admin\Controller;use Think\Controller;class TeamController extends AuthController{	public function index(){		$M_team = M('department');		$teams = $M_team->field('dep_id,dep_name,status')->select();		$this->assign('teams',$teams);		$this->assign('Syn',$_SESSION['Syn']);		$this->display();	}	//设置月目标部门	function set_dep(){		$dep_id = I('dep_id');		$status = I('status');		$M_team = M('department');		if($status == 0){			$M_team->where("dep_id={$dep_id}")->save(array('status'=>1));			$this->error('成功设置为月目标部门',U('Team/index'),1);		}else{			$M_team->where("dep_id={$dep_id}")->save(array('status'=>0));			$this->error('成功取消月目标部门',U('Team/index'),1);		}		//redirect('Team/index');	}}